import { CHANGE_INPUT, CHANGE_LIST, GET_LIST} from './actionTypes';
import axios from 'axios';

export const changeInputAction = (value) => ({
  type: CHANGE_INPUT,
  value,
})

export const changeListAction = (value) => ({
  type: CHANGE_LIST,
  value,
})

export const getListAction = (value) => ({
  type: GET_LIST,
  value,
})

export const getTodoList = () =>{
  return (dispatch)=>{
      axios.get('https://www.fastmock.site/mock/310b5d0181ece0e7cd2383574b9567ad/anita/getList').then((res)=>{
          const data = res.data
          const action = getListAction(data)
          dispatch(action)
      })
  }
}
