// 这句话可以使用imrc快速生成
// 也可以使用aaa快速生成
import React, {Component} from 'react';
// 引入css样式
import 'antd/dist/antd.css';
import store from './store';
import { changeInputAction, changeListAction, getListAction} from './store/actionCreators';
import TodoListUI from './TodoListUI';
import axios from 'axios';

class TodoList extends Component {
  constructor(props){
    super(props);
    this.state = store.getState();
    // 订阅Redux的状态
    store.subscribe(this.storeChange);
  }

  componentDidUpdate(){
    axios.get('https://www.baidu.com').then((res)=>{
        const data = res.data;
        const action = getListAction(data);
        store.dispatch(action);
    })
  }

  storeChange = () => {
    this.setState(store.getState());
  }

  changeInputValue = (e) => {
    const action = changeInputAction(e.target.value);

    // 通过 dispatch() 方法传递给 store
    // 这时 Action就已经完全创建完成了，也和store有了联系
    store.dispatch(action);
  }

  clickBtn = () => {
    this.state.list.push(this.state.inputValue)
    const lists = changeListAction(this.state.list);
    const action = changeInputAction('');

    store.dispatch(lists);
    store.dispatch(action);
  }

  deleteItem = (index) => {
    this.state.list.splice(index, 1);
    const lists = changeListAction(this.state.list);

    store.dispatch(lists);
  };

  render() { 
    return ( 
      <TodoListUI 
        inputValue={this.state.inputValue}
        list={this.state.list}
        changeInputValue={this.changeInputValue}
        clickBtn={this.clickBtn}
        deleteItem={this.deleteItem}
      />
    );
  }
}

export default TodoList;
